insert into content_section (id, version, author_id, content, date_created, description, last_updated, name, page_id, sequence, template_id, title, status) values (39, 14, 1, '<p><span style="font-family: comic sans ms,sans-serif; font-size: x-small;"><span style="font-family: comic sans ms,sans-serif; font-size: small;"><span style="font-family: comic sans ms,sans-serif;">ESMZone is THE online community game that encourages leadership and mentoring among student athletes. We engage 13-20 year old athletes across the nation in a competition to win prizes in a sports-centric social media environment that fosters and rewards positive participation. </span></span></span></p>
