insert into content_section (id, version, author_id, content, date_created, description, last_updated, name, page_id, sequence, template_id, title, status) values (45, 5, 1, '<p><strong><em><span style="font-size: small;"><span style="font-family: comic sans ms,sans-serif;"><span style="font-size: x-small;"><span style="font-family: comic sans ms,sans-serif; font-size: small;">Leadership - Teamwork - Fun - Excitement - Passion - Enthusiasm - Commitment - Character - Integrity</span></span></span></span></em></strong></p>
