insert into content_section (id, version, author_id, content, date_created, description, last_updated, name, page_id, sequence, template_id, title, status) values (46, 3, 1, '<p><span style="font-family: comic sans ms, sans-serif; font-size: small;"><span style="font-family: comic sans ms,sans-serif;">ESMZone is a community game built around a simple formula - Ask good questions; earn points.&nbsp; Provide&nbsp;valuable answers; earn more points; Compete to win exciting&nbsp;prizes and earn medals for helping&nbsp;other athletes and friends.&nbsp;&nbsp;Pretty <strong><em>simple</em></strong> and <strong><em>fun</em></strong>.&nbsp;&nbsp;The more you do, the more you get.&nbsp;&nbsp;Top point winners take home the loot.</span></span></p>', '2010-12-02 00:00:00', 'Simple description of game', '2010-12-02 15:17:03', 'New How it Works', 2, 2, null, 'How it Works...Ask. Answer. WIN!', 'ACTIVE');
insert into content_section (id, version, author_id, content, date_created, description, last_updated, name, page_id, sequence, template_id, title, status) values (47, 2, 1, '<p><span style="font-family: comic sans ms,sans-serif; font-size: small;">Yes, we are on a mission.&nbsp; We created ESMZone&nbsp;to provide student athletes&nbsp;an environment where they are rewarded for&nbsp;communicating, cooperating, and competing in a community of peers inspiring and informing each other.</span>
