insert into content_section (id, version, author_id, content, date_created, description, last_updated, name, page_id, sequence, template_id, title, status) values (54, 0, 1, '<p><strong><em><span style="font-size: small;">Teamwork - Fun - Excitement - Passion - Enthusiasm - Commitment - Character - Integrity</span></em></strong></p>
